select RTRIM(TEACHERS.TEACHERNO) as jsh,TEACHERS.NAME as jsxm,SCHOOLS.NAME as jsxy,POSITIONS.VALUE as zc,TEACHERTYPE.VALUE as gwlb FROM [work] as w inner join TEACHERS on w.TeacherNo=TEACHERS.TEACHERNO INNER JOIN SCHOOLS ON TEACHERS.SCHOOL=SCHOOLS.SCHOOL
INNER JOIN POSITIONS ON POSITIONS.NAME=TEACHERS.POSITION INNER JOIN TEACHERTYPE ON TEACHERS.TYPE=TEACHERTYPE.NAME WHERE w.Totalwork_id=:totalwork