select RTRIM(TEACHERS.TEACHERNO) as jsh,TEACHERS.NAME as jsxm,SCHOOLS.NAME as jsxy,SCHOOLS.SCHOOL,POSITIONS.VALUE as zc,TEACHERTYPE.VALUE as gwlb FROM TEACHERS INNER JOIN SCHOOLS ON TEACHERS.SCHOOL=SCHOOLS.SCHOOL
INNER JOIN POSITIONS ON POSITIONS.NAME=TEACHERS.POSITION INNER JOIN TEACHERTYPE ON TEACHERS.TYPE=TEACHERTYPE.NAME WHERE TEACHERS.TEACHERNO=:TEACHERNO