select * from(select row_number() over(order by TEACHERS.SCHOOL) as row,TEACHERS.NAME,TEACHERS.TEACHERNO,SCHOOLS.NAME AS SCHOOL from TEACHERS left join SCHOOLS on TEACHERS.SCHOOL=SCHOOLS.SCHOOL
where TEACHERS.NAME like :TEACHERNAME and TEACHERS.TEACHERNO LIKE :TEACHERNO AND TEACHERS.SCHOOL LIKE :SCHOOL
) as b where b.row between :start and :end